generated.css:
@font-face {
  font-family: Heroic;
  font-style: italic;
  font-weight: 500;
  src: url("https://cdn.vox-cdn.com/shared_fonts/unison/verge/heroic-cond-vrg-web-ltd-md-obq.woff2");
}
@font-face {
  font-family: Heroic;
  font-style: normal;
  font-weight: 500;
  src: url("https://cdn.vox-cdn.com/shared_fonts/unison/verge/heroic-cond-vrg-web-ltd-md.woff2");
}
@font-face {
  font-family: Pathways;
  font-style: normal;
  font-weight: 400;
  src: url("https://cdn.vox-cdn.com/shared_fonts/unison/verge/pathways-normal-webfont.woff2");
}
html,
body,
figure,
h1,
h2,
p {
  margin: 0;
}
html {
  font-size: 18px;
  font-family: Helvetica, sans-serif;
  text-size-adjust: 100%;
}
* {
  box-sizing: border-box;
}
article {
  display: grid;
  grid-template-columns: auto 85px 85px 762px 85px 85px auto;
}
@media screen and (max-width: 1150px) {
  article {
    grid-template-columns: auto 0 60px calc(100% - 60px * 2) 60px 0 auto;
  }
}
@media screen and (max-width: 599px) {
  article {
    grid-template-columns: auto 0 20px calc(100% - 20px * 2) 20px 0 auto;
  }
}
article > * {
  grid-column: 4;
}
article > figure {
  grid-column: 1/-1;
  width: 100vw;
  max-width: 100%;
  margin: 2.2rem 0;
}
article > figure.hero {
  margin: 0 0 1.2rem 0;
}
h1 {
  font-family: Heroic;
  font-weight: 700;
  font-size: 3.5rem;
  letter-spacing: 0.008rem;
  line-height: 0.9;
  margin-bottom: 0.6rem;
}
h2 {
  font-family: Heroic;
  font-weight: 400;
  font-size: 1.2rem;
  color: #6a6a6a;
  margin-bottom: 0.6rem;
}
p {
  font-size: 1rem;
  font-weight: 300;
  margin-bottom: 1.2rem;
  line-height: 1.6;
}
p.large {
  font-size: 1.2rem;
}
p.has-dropcap:first-letter {
  font-family: Pathways;
  font-size: 6.24rem;
  line-height: 1;
  float: left;
  margin: 0 0.1em -0.1em 0;
}
q {
  position: relative;
  font-family: Heroic;
  font-size: 2rem;
  font-style: italic;
  font-weight: 600;
  text-transform: uppercase;
  color: #383092;
  margin-bottom: 1.2rem;
  padding: 1rem 1.25rem 0 0;
}
q:before {
  position: absolute;
  top: 1px;
  right: 20px;
  content: '';
  height: 2px;
  width: 35px;
  background: #000;
  transform: rotate(-120deg);
  transform-origin: top right;
}
q:after {
  position: absolute;
  top: 0;
  left: 0;
  right: 19px;
  height: 2px;
  background: #000;
}
.labels {
  margin: 0 0 1.2rem 0;
  padding: 0;
}
.labels li {
  position: relative;
  display: inline-block;
  text-transform: uppercase;
  margin: 15px -9px 4px 6px;
  color: #ec008a;
}
.labels li:before {
  position: absolute;
  left: 0;
  top: 0;
  width: 100%;
  height: 100%;
  content: '';
  border-right: 1px solid;
  border-top: 1px solid;
  transform: skew(25deg);
}
.labels li a {
  display: block;
  font-family: Heroic;
  text-decoration: none;
  padding: 8px 14px 0px 14px;
  color: inherit;
}
